import React, { useState } from 'react';
import styled from 'styled-components'

const SliderWrapper = styled.div`
    display: inline-block;
    position: relative;
    width: 60px;
    height: 24px;
`;



// ${CheckBox}:focus & {
//     box-shadow: 0 0 1px #2196F3;



// } 
// ${CheckBox}:checked & {
//     -webkit-transform: translateX(26px);
//     -ms-transform: translateX(26px);
//     transform: translateX(26px);
// }



const SliderRound = styled.span`
        position: absolute;
        cursor: pointer;
        top: 0;
        left: 0;
        right: 0;
        bottom: 0;
        // background-color: #ccc;
        -webkit-transition: .6s;
        transition: .6s;
        border-radius: 24px;

        &:before {
            border-radius: 50%;
            position: absolute;
            content: "";
            height: 30px;
            width: 30px;
            left: 1px;
            bottom: -3px;
            // background-color: white;
            background-color: ${props => props.fColor};
            -webkit-transition: .6s;
            transition: .6s;
        }
        background-color: ${props => props.bColor};
`;

const Slider = () => {

    const [isChecked, setIsChecked] = useState(true);

    return (
        <div>
            <SliderWrapper>
                <SliderRound bColor={isChecked ? '#6da4ed' : '#ccc'} fColor={isChecked ? '#1a73e8' : 'white'} 
                onMouseDown={() => { setIsChecked(!isChecked) }} />
            </SliderWrapper>

            <input type="checkbox" onChange={() => console.log('ooooo', isChecked)} />
        </div>




    );

}




export { Slider };
