function createNewRow() {
    var newForm = document.createElement('form'),
        target = document.querySelector('div#ad-units'),
        inner = '<div class=\"mdl-textfield mdl-js-textfield\">\r\n\t<input class=\"mdl-textfield__input\" type=\"text\" id=\"sample1\">\r\n\t<label class=\"mdl-textfield__label\" for=\"sample1\">sitename-lb<\/label>\r\n<\/div>\r\n<div class=\"mdl-textfield mdl-js-textfield\">\r\n\t<input class=\"mdl-textfield__input\" type=\"text\" id=\"sample1\">\r\n\t<label class=\"mdl-textfield__label\" for=\"sample1\">728x90,970x250,320x50<\/label>\r\n<\/div>\r\n<div class=\"mdl-textfield mdl-js-textfield\">\r\n\t<input class=\"mdl-textfield__input\" type=\"text\" id=\"sample1\">\r\n\t<label class=\"mdl-textfield__label\" for=\"sample1\">uap-sitename, wp-sitename<\/label>\r\n<\/div>';

    newForm.innerHTML = inner;
    target.appendChild(newForm);
}

function removeComma(string) {
    var removed = string.replace(/,/g, ';');
    return removed;
}

export const generateCsv = (rowData) => {
    const data = [];
    rowData.forEach((ob, index, arr) => {

        var wSitePlacement = ob.website ? ob.website + ";" : "" + ob.placement.replace(/,/g, ';');
        const temp = [ob.unit, ob.unit, ob.sizes, ",", wSitePlacement.trim(), "_blank,"];
        data.push(temp);
    });
    console.log('rowData', data);


    var csv = '';
    data.forEach(function (row) {
        csv += row.join(',');
        csv += "\n";
    });

    console.log(csv);
    var hiddenElement = document.createElement('a');
    hiddenElement.href = 'data:application/octet-stream,' + encodeURIComponent(csv);
    hiddenElement.target = '_blank';
    hiddenElement.download = 'BulkUnits.csv';
    hiddenElement.click();
}
