import React, { useState } from 'react';
import { Label } from '../LabelAndFont';
import styled from 'styled-components'
import { StandardComponentWrapper } from '../Wrapper';
import { InputText } from '../InputOutputFields';

const InputWLabel = (props) => (
    <StandardComponentWrapper>
        <Label>
            {props.name}
        </Label>
        <InputText margin={props.margin} style={{ width: props.width ? props.width : '100%' }} placeholder={props.placeholder} />
    </StandardComponentWrapper >
);


export { InputWLabel };
