import React, { useState } from 'react';
import styled from 'styled-components'




const Label = styled.span`
    font-size: 20px;
    font-weight: 500
    letter-spacing: .25px;
 `;

const SliderWrapper = styled.label`
    display: inline-block;
    position: relative;
    width: 60px;
    height: 24px;
    // margin: 0 0 -5.5px ${props => props.distance };
    margin: 0 0 -5.5px ${props => (props.distance ? '8px' : props.distance)};

`;

const Checkbox = styled.input`
    opacity: 0;
    position: absolute;
    top: 0;
    left: 0;
    right: 0;
    bottom: 0;
`;


const SliderRound = styled.span`
    position: absolute;
    cursor: pointer;
    top: 0;
    left: 0;
    right: 0;
    bottom: 0;
    // background-color: #ccc;
    -webkit-transition: .6s;
    transition: .6s;
    border-radius: 24px;
    background-color: ${props => props.bColor};

    &:before {
        border-radius: 50%;
        position: absolute;
        content: "";
        height: 30px;
        width: 30px;
        left: 1px;
        bottom: -3px;
        // background-color: white;
        background-color: F8F8FF;
        background-color: ${props => props.fColor};
        -webkit-transition: .6s;
        transition: .6s;

        ${props => props.checked && ({
        '-webkit-transform': 'translateX(27px)',
        '-ms-transform': 'translateX(27px)',
        transform: 'translateX(27px)',
    })}
    }
`;

const Slider = (props) => {

    const [isChecked, setIsChecked] = useState(true);
    return (
        <div>
            <Label>
                {props.name}
            </Label>
            <SliderWrapper distance={props.distance}>
                <SliderRound bColor={isChecked ? '#6da4ed' : '#ccc'} fColor={isChecked ? '#1a73e8' : '#F8F8FF'} checked={isChecked ? true : false} />
                <Checkbox type="checkbox" onChange={() => { console.log('ssss'); setIsChecked(!isChecked) }} />
            </SliderWrapper>
        </div >
    );
}



export { Slider };
