import React, { useState } from 'react';
import styled from 'styled-components'
import { Label } from '../Label';


const ComponentWrapper = styled.div`
    margin-bottom: 10px;
`;

const SelectWrapper = styled.label`
    display: inline-block;
    position: relative;
    // font-size: 20px;
    // font-weight: 500
    // letter-spacing: .25px;
    width: ${props => (props.width ? props.width : '80px')};
    height: 24px;
    margin: 0 0 -5.5px ${props => (props.distance ? props.distance : '10px')};
    // padding: 2px 16px;
`;


const Select = styled.select`
    // font-size: 20px;
    // font-weight: 500
    // letter-spacing: .25px;
    // width: ${props => (props.width ? props.width : '80px')};
    // height: 24px;
    // margin: 0 0 -5.5px ${props => (props.distance ? props.distance : '10px')};
    // border: none;
    // border-bottom: 1px solid ;
    // border-radius: none;
    // border-radius: 0px;



    // opacity: 0;
    position: absolute;
    top: 0;
    left: 0;
    right: 0;
    bottom: 0;

`;

const Option = styled.option`
    // padding: 0 0 -60px; 
    // border-radius: 0px;



`;

const Dropdown = (props) => (
    <ComponentWrapper>
        <Label>
            {props.name}
        </Label>
        <SelectWrapper>
            <Select>
                {props.sspNames.map((x, y) => <Option key={y + x}>{x}</Option>)};
            </Select>
        </SelectWrapper>
    </ComponentWrapper >
);

export { Dropdown };
