import React, { useState } from 'react';
import styled from 'styled-components';
import { AddButton } from '../styles/Buttons';
import { InputText } from '../styles/InputOutputFields';
import { ColumnFlexWrapper, SimpleFlexWrapper, WrapAreas } from '../styles/FlexWrapper';
import { Label } from '../styles/LabelAndFont';
import { Button } from '../styles/Buttons';



// const Card = styled.div`
//   ${rounded}
//   ${column}
// `
const ComponentWrapper = styled.div`

    display: flex;
    justify-content: center;
    margin: 20px 0 ;

    @media(max-width: 862px) {
        margin-left: 280px;
        display: block
    }
`;

const InputWrapper = styled.div`
`;

const WebStiteName = (props) => (
    <ComponentWrapper>
        <InputWrapper >
            <InputText placeholder="Website Name" />
        </InputWrapper>
    </ComponentWrapper>
);

const AddUnits = (props) => (
    <SimpleFlexWrapper top="20px" justify="space-between">
        <InputText placeholder="unit" {...props} />
        <InputText placeholder="sizes" {...props} />
        <InputText placeholder="placement" {...props} />
    </SimpleFlexWrapper>
);

const addUnits = (event) => {
}

const unitChanged = (event) => {
    console.log('in addUnits', event.target.value);
}

const CsvGeneration = () => {
    // Declare a new state variable, which we'll call "count"
    const [units, setUnits] = useState([{ unit: { sizes: '', placements: '' } }]);
    const generate = () => {
        console.log('bum dingsing');
    }

    return (
        <SimpleFlexWrapper top="30px">
            <WrapAreas adjust="true">
                <WebStiteName />
                {units.map((ele, index) => (
                    <AddUnits key={ele.unit + index} onChange={unitChanged} />
                ))}
                <AddButton justify={'center'} top={'50px'} />
                <Button name={"Generate"} onClick={generate} top="80px" justify="center" />
            </WrapAreas>
        </SimpleFlexWrapper>
    );
}

export { CsvGeneration };
