import React, { useState } from 'react';
import styled from 'styled-components';
import { AddButton } from '../styles/Buttons';
import { InputText } from '../styles/InputOutputFields';
import { ColumnFlexWrapper, SimpleFlexWrapper, WrapAreas } from '../styles/FlexWrapper';
import { Label } from '../styles/LabelAndFont';
import { Button } from '../styles/Buttons';



// const Card = styled.div`
//   ${rounded}
//   ${column}
// `
const ComponentWrapper = styled.div`

    display: flex;
    justify-content: center;
    margin: 20px 0 ;

    @media(max-width: 862px) {
        margin-left: 280px;
        display: block
    }
`;

const InputWrapper = styled.div`
`;

const WebStiteName = (props) => (
    <ComponentWrapper>
        <InputWrapper >
            <InputText placeholder="Website Name" />
        </InputWrapper>
    </ComponentWrapper>
);

const AddUnits = (props) => (
    <SimpleFlexWrapper top="20px" justify="space-between">
        <InputText name={props.unit} placeholder="unit" {...props} />
        <InputText name={props.unit} placeholder="sizes" {...props} />
        <InputText name={props.unit} placeholder="placement" {...props} />
    </SimpleFlexWrapper>
);


const CsvGeneration = () => {
    // Declare a new state variable, which we'll call "count"
    const [units, setUnits] = useState([{ unit: '', sizes: '', placement: '' }]);
    const [currentUnits, setCurrentUnits] = useState({ unit: '', sizes: '', placement: '' });
    const generate = () => {
        console.log('bum dingsing');
    }

    const addUnits = (event) => {
    }

    const unitChanged = (event) => {
        // if

        var unit = currentUnits;
        switch (event.target.placeholder) {
            case 'unit':
                unit.unit = event.target.value;
                break;
            case 'sizes':
                unit.sizes = event.target.value;
                break;
            case 'placement':
                unit.placement = event.target.value;
        }
        setCurrentUnits(unit);
        console.log('id:: ', event.target.name, 'in addUnits: ', currentUnits);
    }

    return (
        <SimpleFlexWrapper top="30px">
            <WrapAreas adjust="true">
                <WebStiteName />
                {units.map((ele, index) => (
                    <AddUnits key={ele.unit} name={ele.unit} onChange={unitChanged} />
                ))}
                <AddButton justify={'center'} top={'50px'} />
                <Button name={"Generate"} onClick={generate} top="80px" justify="center" />
            </WrapAreas>
        </SimpleFlexWrapper>
    );
}

export { CsvGeneration };
