import React, { useState } from 'react';
import styled from 'styled-components'

const SliderWrapper = styled.div`
    display: inline-block;
    position: relative;
    width: 60px;
    height: 24px;
`;



// ${CheckBox}:focus & {
//     box-shadow: 0 0 1px #2196F3;



// } 
// ${CheckBox}:checked & {
//     -webkit-transform: translateX(26px);
//     -ms-transform: translateX(26px);
//     transform: translateX(26px);
// }



const SliderRound = styled.span`
        position: absolute;
        cursor: pointer;
        top: 0;
        left: 0;
        right: 0;
        bottom: 0;
        // background-color: #ccc;
        -webkit-transition: .6s;
        transition: .6s;
        border-radius: 24px;

        &:before {
            border-radius: 50%;
            position: absolute;
            content: "";
            height: 30px;
            width: 30px;
            left: 1px;
            bottom: -3px;
            // background-color: white;
            background-color: ${props => props.fColor};
            -webkit-transition: .6s;
            transition: .6s;

            ${props => props.checked && ({
        '-webkit-transform': 'translateX(27px)',
        '-ms-transform': 'translateX(27px)',
        transform: 'translateX(27px)',
    })}
        }
        background-color: ${props => props.bColor};
`;

const Slider = (props) => {

    const [isChecked, setIsChecked] = useState(true);

    const isChangedFunc = () => {

        var ev = new Event('input', { bubbles: true });
        ev.simulated = true;
        SliderRound.value = isChecked;
        SliderRound.dispatchEvent(ev);
    }


    return (
        <div>
            <SliderWrapper>
                <SliderRound bColor={isChecked ? '#6da4ed' : '#ccc'} fColor={isChecked ? '#1a73e8' : '#F8F8FF'}
                    checked={isChecked ? true : false} onMouseDown={() => { setIsChecked(!isChecked) }} />
            </SliderWrapper>

        </div>




    );

}




export { Slider };
