import styled from 'styled-components'


const InputArea = styled.textarea`
    border: 2px solid #1a73e8;
    border-radius: 4px;
    font-size: 14px;

`;


const OutputArea = styled.div`
    border: 2px solid #1a73e8;
    border-radius: 4px;
    font-size: 14px;

`;

const InputField = styled.input`
    border: 2px solid #1a73e8;
    border-radius: 4px;
    font-size: 14px;
`;

export { InputArea, OutputArea, InputField }
