import React, { useState, useEffect } from 'react';
import styled from 'styled-components';
import { InputArea, OutputArea } from '../styles/Areas'
import { Button } from '../styles/Button'
import { Slider } from '../styles/molecules/Slider'
import { PageOuterWrapper, SimpleFlexWrapper } from '../styles/PageWrapper'
import { Dropdown } from '../styles/molecules/Dropdown';

const InputField = styled(InputArea)`

    height: 30vh;
    margin-bottom: 30px;
    margin-top: 10px;
`;

const OutputField = styled(OutputArea)`

    height: 30vh;
`;

const WrapAreas = styled(PageOuterWrapper)`
    width: 60%;
`;

const ConfigGeneration = () => {
    // Declare a new state variable, which we'll call "count"
    const [output, setOutput] = useState("Output");
    const [isClipBChecked, setIsClipBChecked] = useState(true);
    const [isRopChecked, setIsRopChecked] = useState(true);



    const sspNames = ['criteo', 'yieldlab'];
    const configResult = () => {
        // setOutput();
    }

    const ConfigSetting = styled(SimpleFlexWrapper)`
        justify-content: flex-start;
        justify-content: space-between;
        max-width: 60%;
        @media(max-width: 1439px) {
            max-width: 70%;
        }
        @media(max-width: 1400px) {
            flex-flow: column;
            min-width: 229px
        }
    `;

    // //  <Slider name={"ROP"} /* changeDesign={true} */ checked={isRopChecked} onChange={() => setIsRopChecked(!isRopChecked)} /> 
    return (
        <SimpleFlexWrapper top="30px">
            <WrapAreas>
                <ConfigSetting>
                    <Slider name={"ROP"} checked={isRopChecked} onChange={() => setIsRopChecked(!isRopChecked)} />
                    <Slider name={"Save to clipboard"} checked={isClipBChecked} onChange={() => setIsClipBChecked(!isClipBChecked)} />
                    <Dropdown name={"Choose SSP"} sspNames={sspNames} />
                </ConfigSetting>
                <InputField placeholder="Input" />
                <OutputField style={{ color: (output === "Output") ? " grey" : null }} dangerouslySetInnerHTML={{ __html: output }}></OutputField>
                <Button name={"Generate"} />
            </WrapAreas>
        </SimpleFlexWrapper>
    );
}

export { ConfigGeneration };
