import React, { useState, useEffect, useRef } from 'react';
import styled from 'styled-components';
import { InputArea, OutputArea } from '../styles/InputOutputFields';
import { Button } from '../styles/Buttons';
import { Slider } from '../styles/molecules/Slider';
import { ColumnFlexWrapper, SimpleFlexWrapper, WrapAreas } from '../styles/FlexWrapper';
import { Dropdown } from '../styles/molecules/Dropdown';
// import { configGenration } from '../services/ConfigGeneration'
import { configGeneration } from '../services/CGenerationService';


const InputField = styled(InputArea)`
    flex: 0 0 49%;
    height: 30vh;
    margin-bottom: 28px;
    margin-top: 14px;
    outline: none;
    @media(max-width: 1363px) {
        flex: 0 0 48%;
    }
    @media(max-width: 550) {
        flex: 0 0 46%;
    }
`;

const DistanceInput = styled.div`
    width: 1%;
`;

const OutputField = styled(OutputArea)`
    height: 30vh;
`;

const ConfigSetting = styled(SimpleFlexWrapper)`
    justify-content: flex-start;
    justify-content: space-between;
    max-width: 60%;
    @media(max-width: 1439px) {
            max-width: 70%;
    }
    @media(max-width: 1400px) {
            flex-flow: column;
            min-width: 229px
    }
`;

const inputFromValue = (configString, slotString) => {
    var _config, _slots;
    if (typeof configString === "string") {
        _config = configString.replace(/([a-zA-Z0-9]+?):/g, '"$1":');
        _config = _config.replace(/'/g, '"');
        try {
            _config = JSON.parse(_config);
        } catch (ex) {
            alert('Config is not valid JSON \nPlease check the console for more detail');
            console.error(ex);
        }
    }
    if (typeof slotString === "string") {
        _slots = slotString.replace(/([a-zA-Z0-9]+?):/g, '"$1":');
        _slots = _slots.replace(/'/g, '"');
        try {
            _slots = JSON.parse(_slots);
        } catch (ex) {
            alert('Slots are not valid JSON \nPlease check the console for more detail');
            console.error(ex);
        }
    }
    return { config: _config ? _config : [], slots: _slots ? _slots : [] };

}

const ConfigGeneration = () => {
    // Declare a new state variable, which we'll call "count"
    const [output, setOutput] = useState("Output");
    const [inConfig, setInConfig] = useState([]);
    const [inSlots, setInSlots] = useState([]);
    const [isClipBChecked, setIsClipBChecked] = useState(true);
    const [isRopChecked, setIsRopChecked] = useState(true);
    const [sSp, setSSp] = useState('criteo');
    const clipboardRef = useRef(null);

    const sspNames = ['criteo', 'yieldlab'];
    const configResult = () => {
        // setOutput();
    }

    const generate = () => {

        // var te = inputFromValue(inConfig.slice(), inSlots.slice());
        // console.log(te.config, '  ', te.slots);

        const outText = configGeneration(inputFromValue(inConfig, inSlots), sSp, isRopChecked);
        setOutput(outText);
        // setOutput(configGeneration(inputFromValue(inConfig, inSlots), sSp, isRopChecked));
        // clipboardRef.current.select();
        // outText.select();
        // document.addEventListener('copy', function (e) {
        //     // e.clipboardData is initially empty, but we can set it to the
        //     // data that we want copied onto the clipboard.
        //     e.clipboardData.setData('text/plain', 'outTexöööt');
        //     // e.clipboardData.setData('text/html', '<b>Hello, world!</b>');

        //     // This is necessary to prevent the current document selection from
        //     // being written to the clipboard.
        //     e.preventDefault();
        // });
        // var data = new Blob(["Text data"], {type : "text/plain"});

        // navigator.clipboard.write(data).then(function() {
        //     console.log("Copied to clipboard successfully!");
        //   }, function() {
        //     console.error("Unable to write to clipboard.");
        //   });

        navigator.clipboard.writeText("bhhh").then(function () {
            console.log("Copied to clipboard successfully!");

        }, function () {
                console.error("Unable to write to clipboard.");
        });


    }

    return (
        <SimpleFlexWrapper top="50px">
            <WrapAreas>
                <ConfigSetting>
                    <Slider name={"ROP"} changeDesign={true} checked={isRopChecked} onChange={() => setIsRopChecked(!isRopChecked)} />
                    <Slider name={"Save to clipboard"} checked={isClipBChecked} onChange={() => setIsClipBChecked(!isClipBChecked)} />
                    <Dropdown name={"Choose SSP"} sspNames={sspNames} onChange={(e) => setSSp(e.target.value)} />
                </ConfigSetting>
                <SimpleFlexWrapper>
                    <InputField placeholder="Input Config: openX" onChange={(e) => setInConfig(e.target.value)} />
                    < DistanceInput />
                    <InputField placeholder="Input Slots: openX" onChange={(e) => setInSlots(e.target.value)} />
                </SimpleFlexWrapper>
                <OutputField ref={clipboardRef} style={{ color: (output === "Output") ? " grey" : null }} dangerouslySetInnerHTML={{ __html: output }}></OutputField>
                <Button name={"Generate"} onClick={generate} top="14px" justify="flex-end" />
            </WrapAreas>
        </SimpleFlexWrapper>
    );
}

export { ConfigGeneration };
