import React, { useState } from 'react';
import { Label } from '../LabelAndFont';
import styled from 'styled-components'
import {StandardComponentWrapper} from './Wrapper';


const InputArea = styled.textarea`
    border: 2px solid #1a73e8;
    border-radius: 4px;
    font-size: 14px;

`;

const OutputArea = styled.div`
    border: 2px solid #1a73e8;
    border-radius: 4px;
    font-size: 14px;
    overflow: scroll;

`;

const InputText = styled.input`
    flex: 1 1;
    outline: none;
    border: none;
    border-bottom: 1px solid  #202024;
    // font-size: 14px;
    font-size: 20px;
    margin: 0 ${props => props.margin ? '1%' : '0'};
    padding:  2px 0px ;
    height: 22px
    placeholder: ${props => props.placeholder ? props.placeholder : ''}
`;


const Slider = (props) => (
    <StandardComponentWrapper>
        <Label>
            {props.name}
        </Label>
        <SliderWrapper distance={props.distance} changeDesign={props.changeDesign}>
            <SliderRound bColor={props.checked ? '#6da4ed' : '#ccc'} fColor={props.checked ? '#1a73e8' : '#F8F8FF'} checked={props.checked} />
            <Checkbox type="checkbox" {...props} />
        </SliderWrapper>
    </StandardComponentWrapper >
);

export { InputArea, OutputArea, InputText }
