import React, { useState, useEffect } from 'react';
import styled from 'styled-components';
import { AddButton } from '../styles/Buttons';
import { InputText } from '../styles/InputOutputFields';
import { SimpleFlexWrapper, WrapAreas } from '../styles/FlexWrapper';
import { Button, ButtonElement } from '../styles/Buttons';


// Minus button - -
const ComponentWrapper = styled.div`
    display: flex;
    justify-content: center;
    margin: 20px 0 ;

    @media(max-width: 862px) {
        margin-left: 280px;
        display: block
    }
`;

const InputWrapper = styled.div`
`;

const ResetButton = styled(ButtonElement)`
    background-color: transparent;
    padding: 2px 8px ;
    margin-left: 25px;
    border-color: #6c757d;
`;
const WebStiteName = (props) => (
    <ComponentWrapper>
        <InputWrapper >
            <InputText placeholder="Website Placement" />
        </InputWrapper>
    </ComponentWrapper>
);



const CsvGeneration = () => {

    const [units, setUnits] = useState([{ unit: '', sizes: '', placement: '' }]);
    const generate = () => {
        console.log('bum dingsing');
    }

    useEffect(() => {
        console.log('effects: ', units);
    });

    const AddUnits = (props) => (
        <SimpleFlexWrapper top="20px" justify="space-between">
            <InputText name={props.pos} placeholder="Unit" value={units[0].unit} {...props} />
            <InputText name={props.pos} placeholder="Sizes" {...props} />
            <InputText name={props.pos} placeholder="Placement"   {...props} />
            <ResetButton name={props.pos} onClick={removeUnit} >Delete  </ResetButton>
        </SimpleFlexWrapper>
    );

    const removeUnit = (event) => {
        const _units = [];
        for (var unitTemp in units) {
            if (unitTemp === event.target.name) {
                continue;
            } else {
                _units.push(units[unitTemp]);
            }
        }
        setUnits(_units);
    }

    const addUnit = (event) => {
        var _units = [...units];
        _units.push({ unit: '', sizes: '', placement: '' });
        setUnits(_units);
    }

    const unitChanged = (event) => {
        var _unit = units[event.target.name];
        switch (event.target.placeholder) {
            case 'Unit':
                _unit.unit = event.target.value;
                break;
            case 'Sizes':
                _unit.sizes = event.target.value;
                break;
            case 'Placement':
                _unit.placement = event.target.value;
        }
        console.log('inchange')
        units[event.target.name] = _unit

        // setUnits([...units]);
    }

    const ch = (e) => {
        // units[0].unit = e.target.value;

        setUnits([{ units: e.target.value }])
    }
    // <InputText key={ele + index} placeholder="Unit" pos={index} value={units[0].unit} onChange={ch} />

    return (
        <SimpleFlexWrapper top="30px">
            <WrapAreas adjust="true">
                <WebStiteName />
                {units.map((ele, index) => (
                    <AddUnits key={ele + index} pos={index} onChange={unitChanged} data={ele} />

                ))}
                <AddButton justify={'center'} top={'50px'} onClick={addUnit} />
                <Button name={"Generate"} onClick={generate} top="80px" justify="center" />
            </WrapAreas>
        </SimpleFlexWrapper>
    );
}

export { CsvGeneration };
