import React, { useState, useEffect } from 'react';
import styled from 'styled-components';
import { AddButton } from '../styles/Buttons';
import { InputText } from '../styles/InputOutputFields';
import { SimpleFlexWrapper, WrapAreas } from '../styles/Wrapper';
import { Button, ButtonElement } from '../styles/Buttons';
import { generateCsv } from '../services/CsvGenerationService';
import { csvPlaceholders } from '../services/Helpers';


// Minus button - -
const ComponentWrapper = styled.div`
    display: flex;
    justify-content: center;
    margin: 20px 0 ;

    @media(max-width: 862px) {
        margin-left: 280px;
        display: block
    }
`;

const InputWrapper = styled.div`
`;

const ResetButton = styled(ButtonElement)`
    background-color: #dc3545;
    padding: 2px 8px ;
    margin-left: 25px;
    border-color: #dc3545;
`;

const WebStiteName = (props) => (
    <ComponentWrapper>
        <InputWrapper >
            <InputText placeholder={csvPlaceholders.WEBSITE} {...props} />
        </InputWrapper>
    </ComponentWrapper>
);

const AddUnits = (props) => (
    <SimpleFlexWrapper top="20px" justify="space-between">
        <InputText name={props.pos} placeholder={csvPlaceholders.UNIT} value={props.unit} {...props} />
        <InputText name={props.pos} placeholder={csvPlaceholders.SIZES} value={props.sizes} {...props} />
        <InputText name={props.pos} placeholder={csvPlaceholders.PLACEMENT} value={props.placement}   {...props} />
        <ResetButton name={props.pos} onClick={props.removeUnit}{...props}> Delete  </ResetButton>
    </SimpleFlexWrapper>
);

const CsvGeneration = (props) => {
    const [units, setUnits] = useState([{ unit: '', sizes: '', placement: '', website: '' }]);
    const [website, setWebsite] = useState('');
    const generate = () => {
        generateCsv([...units]);
    }

    useEffect(() => {
        // if (props.location.state.config)
        // console.log('csv', props.location.state.config[0].params.addStringtocall[1][1]);
        const temp = [{
            "bidder": "criteo", "params":
            {
                "sizes": [{ "size": "300x250", "pl": "medrect1" }, { "size": "300x250", "pl": "medrect2" }, { "size": "728x90", "pl": "leader" }, { "size": "320x100", "pl": "leader" }],
                "placementnameing": "", "addStringtocall": [["networkId", 3724], ["publisherSubId", "s"]]
            }
        }];
        // console.log('csv2', props.location.state.config[0].params.addStringtocall[1][1]);
        // console.log('csv', props.location.state.config,' ss ', props.location.state.config.params.addStringtocall[1][1]);
        /* if (props.location.state.config) {
             const config = JSON.parse(props.location.state.config);
             // setWebsite(config[0].params.addStringtocall[1][1]);
             console.log('csv2', config[0].params.addStringtocall[1][1]);
             const _units = units;
             // config.slots.forEach((el, index) => {
             //     _units[index].unit = el.unit;
             //     _units[index].sizes = el.sizes;
             //     _units[index].placement = '';
             //     _units[index].website = website;
             // })

             // setSlots(props.location.state.slots);
         }*/
    });

    const removeUnit = (event) => {
        const _units = [];
        for (var unitTemp in units) {
            if (unitTemp === event.target.name) {
                continue;
            } else {
                _units.push(units[unitTemp]);
            }
        }
        setUnits(_units);
    }

    const addUnit = (event) => {
        var _units = [...units];
        _units.push({ unit: '', sizes: '', placement: '' });
        setUnits(_units);
    }

    const unitChanged = (event) => {
        var _unit = units[event.target.name];
        switch (event.target.placeholder) {
            case csvPlaceholders.UNIT:
                _unit.unit = event.target.value;
                break;
            case csvPlaceholders.SIZES:
                _unit.sizes = event.target.value;
                break;
            case csvPlaceholders.PLACEMENT:
                _unit.placement = event.target.value;
                break;
            case csvPlaceholders.WEBSITE:
                _unit.website = event.target.value;
        }
    }
    return (
        <SimpleFlexWrapper top="30px">
            <WrapAreas adjust="true">
                <WebStiteName onChange={unitChanged} />
                {/* <WebStiteName website={website} onChange={unitChanged} /> */}
                {units.map((ele, index) => (
                    <AddUnits key={ele + index} pos={index} onChange={unitChanged} removeUnit={removeUnit} />
                ))}
                <AddButton justify={'center'} top={'50px'} onClick={addUnit} />
                <Button name={"Generate"} onClick={generate} top="80px" justify="center" />
            </WrapAreas>
        </SimpleFlexWrapper>
    );
}

export { CsvGeneration };
