import React, { useState } from 'react';
import styled from 'styled-components'
import { SimpleFlexWrapper } from './FlexWrapper'
// import addButton from '../resources/addButton.svg'
import { ReactComponent as AddButtonSVG } from '../resources/addButton.svg';

const ButtonElement = styled.button`
    min-height: 16px;
    font-size: 20px;
    font-weight: 500
    letter-spacing: .25px;
    background-color: #eb3323;
    min-width: 100px;
    border-radius: 4px;
    padding: 9px 16px;
    margin-left: 5px ;
    text-align: center;
    outline: none;
    &:active {
        transform: translateY(4px);
    }
`;

const Button = (props) => (
    <SimpleFlexWrapper justify={'flex-end'} top={'14px'}>
        <ButtonElement  {...props} >{props.name}</ButtonElement>
    </SimpleFlexWrapper>
);

const ButtonAddElement = styled(ButtonElement)`
    min-width: 0;
    background-color: transparent;

    width: ${props => props.size ? props.size : '50px'};
    height: ${props => props.size ? props.size : '50px'};
    border-radius: 40%;

`;

const AddButton = (props) => (
    <SimpleFlexWrapper justify={'flex-end'} top={'14px'}>
        <ButtonAddElement size={props.size} {...props}>
            <AddButtonSVG />
        </ButtonAddElement>
    </SimpleFlexWrapper>
);


export { Button, AddButton };
