import React, { useState, useEffect } from 'react';
import ReactDOM from "react-dom";
import styled from 'styled-components';
import { AddButton } from '../styles/Buttons';
import { InputText } from '../styles/InputOutputFields';
import { ColumnFlexWrapper, SimpleFlexWrapper, WrapAreas } from '../styles/FlexWrapper';
import { Label } from '../styles/LabelAndFont';
import { Button } from '../styles/Buttons';
import { testNameToKey } from 'jest-snapshot/build/utils';



// const Card = styled.div`
//   ${rounded}
//   ${column}
// `
const ComponentWrapper = styled.div`

    display: flex;
    justify-content: center;
    margin: 20px 0 ;

    @media(max-width: 862px) {
        margin-left: 280px;
        display: block
    }
`;

const InputWrapper = styled.div`
`;

const WebStiteName = (props) => (
    <ComponentWrapper>
        <InputWrapper >
            <InputText placeholder="Website Name" />
        </InputWrapper>
    </ComponentWrapper>
);

const AddUnits = (props) => (
    <SimpleFlexWrapper top="20px" justify="space-between">
        <InputText name={props.unit} placeholder="unit" {...props} />
        <InputText name={props.unit} placeholder="sizes" {...props} />
        <InputText name={props.unit} placeholder="placement"   {...props} />
    </SimpleFlexWrapper>
);

{/* <InputText placeholder="unit" value={props.unit.unit ? props.unit.unit : undefined} {...props} />
        <InputText placeholder="sizes" value={props.unit.sizes ? props.unit.sizes : undefined} {...props} />
        <InputText placeholder="placement" value={props.unit.placement ? props.unit.placement : undefined}  {...props} /> */}
const CsvGeneration = () => {
    // Declare a new state variable, which we'll call "count"
    const [units, setUnits] = useState([{ unit: '', sizes: '', placement: '' }]);
    const [currentUnit, setCurrentUnit] = useState({ unit: '', sizes: '', placement: '' });

    const generate = () => {
        console.log('bum dingsing');
    }

    useEffect(() => {
        console.log('effects: ', units);
    });

    const addUnits = (event) => {
        if (currentUnit.unit) {
            console.log('jjjjj:: ', currentUnit.unit);
            const _units = [...units, { ...currentUnit }];
            [_units[_units.length - 1], _units[_units.length - 2]] = [_units[_units.length - 2], _units[_units.length - 1]];
            setCurrentUnit({ unit: '', sizes: '', placement: '' });
            console.log('_Units: ', _units);
            setUnits(_units);
        }
        console.log('in add Units: ', units);
    }

    const unitChanged = (event) => {
        var _unit = {};
        var position;
        if (event.target.name === '') {
            _unit = currentUnit;
        }
        else {
            for (var i of units) {
                console.log('unitChanged', JSON.stringify(event.target.name), 'i:::: ',i);
                if (event.target.name === i.units) {
                    console.log('----- in -----');
                    position = i;
                    _unit = units[i];
                    break;
                }
            }
        }

        switch (event.target.placeholder) {
            case 'unit':
                _unit.unit = event.target.value;
                break;
            case 'sizes':
                _unit.sizes = event.target.value;
                break;
            case 'placement':
                _unit.placement = event.target.value;
        }
        setCurrentUnit(_unit);
        console.log('id:: ', event.target.name, 'in unitChanged: ', currentUnit,'position: ', position);
    }

    return (
        <SimpleFlexWrapper top="30px">
            <WrapAreas adjust="true">
                <WebStiteName />
                {units.map((ele, index) => (
                    <AddUnits key={ele + index} name={ele.unit}  onChange={unitChanged} />
                ))}
                <AddButton justify={'center'} top={'50px'} onClick={addUnits} />
                <Button name={"Generate"} onClick={generate} top="80px" justify="center" />
            </WrapAreas>
        </SimpleFlexWrapper>
    );
}

export { CsvGeneration };
