import React, { useState } from 'react';
import styled from 'styled-components';
import { InputArea, OutputArea } from '../styles/InputOutputFields';
import { Button } from '../styles/Buttons';
import { Slider } from '../styles/molecules/Slider';
import { fontSetting } from '../styles/LabelAndFont';
import { InputText } from '../styles/InputOutputFields';
import { ColumnFlexWrapper, SimpleFlexWrapper, WrapAreas } from '../styles/Wrapper';
import { Dropdown } from '../styles/molecules/Dropdown';
// import { configGenration } from '../services/ConfigGeneration'
import { configGeneration } from '../services/CGenerationService';
import { bidder } from '../services/Helper';
import { StandardInput } from '../styles/molecules/StandardInput';


const InputField = styled(InputArea)`
    flex: 0 0 ${props => props.height ? props.height : '30%'};
    margin-bottom: 28px;
    margin-top: 14px;
    outline: none;
    @media(max-width: 1363px) {
        flex: 0 0 48%;
    }
    @media(max-width: 550) {
        flex: 0 0 46%;
    }
`;

const ConfigDropdown = styled(Dropdown)`

`;

const UnitNameWrapper = styled.div`
    margin: 20px 0  50px 0;
`;


const UnitName = (props) => (
    <UnitNameWrapper>
        <InputText margin={props.margin} style={{ width: '100%' }} placeholder="Unit name" />
    </UnitNameWrapper>
);

const InputPageName = styled(InputField)`
    flex: 0 0 150px;
    height: 24px;
    resize: none;
    border: none;
    border-bottom: 1px solid  ;
    padding: 0 0 4.2px;
    margin: 0;
    border-radius: 0;
    overflow: auto;
    white-space: nowrap;
    font-weight: 500;
    ${fontSetting};
    @media(max-width: 1401px) {
        flex: 0 0 10%;
    }
`;

const DistanceInput = styled.div`
    width: 1%;
`;

const OutputField = styled(OutputArea)`
    height: 30vh;
`;

const ConfigSetting = styled(SimpleFlexWrapper)`
    justify-content: flex-start;
    justify-content: space-between;
    max-width: 60%;
    @media(max-width: 1650px) {
         flex-flow: column;
         max-width: 60%;
    }
    `;
    // @media(max-width: 1650px) {
    //         flex-flow: column;
    //         min-width: 229px
    // }
    const ConfigWrapper = styled.div`
    margin: 50px 0 0 0;
`;

const SlotsGeneration = () => {
    // Declare a new state variable, which we'll call "count"
    const [output, setOutput] = useState("Output");
    const [inConfig, setInConfig] = useState([]);
    const [inSlots, setInSlots] = useState([]);
    const [isClipBChecked, setIsClipBChecked] = useState(true);
    const [isRopChecked, setIsRopChecked] = useState(true);
    const [inSsp, setSsp] = useState('criteo');
    const [inPageName, setInPageName] = useState('');

    const [unitName, setUnitName] = useState('');


    const sspNames = [];
    for (let [key, value] of Object.entries(bidder)) {
        sspNames.push(value);
    }


    const inputFromValue = (configString, slotString, pageName) => {
        var _config, _slots;
        var _latePageN = false;
        if (typeof configString === "string") {
            _config = configString.replace(/([a-zA-Z0-9]+?):/g, '"$1":');
            _config = _config.replace(/'/g, '"');
            try {
                _config = JSON.parse(_config);
            } catch (ex) {
                alert('Config is not valid JSON \nPlease check the console for more detail');
                console.error(ex);
            }
        }
        if (typeof slotString === "string") {
            _slots = slotString.replace(/([a-zA-Z0-9]+?):/g, '"$1":');
            _slots = _slots.replace(/'/g, '"');
            try {
                _slots = JSON.parse(_slots);
            } catch (ex) {
                alert('Slots are not valid JSON \nPlease check the console for more detail');
                console.error(ex);
            }
        }
        if (typeof _slots === "string")
            _slots = null;
        if (typeof _config === "string")
            _config = null;
        if (pageName.length < 1) {
            try {
                _latePageN = true;
                pageName = window.prompt('Please insert page name');
            } catch (ex) {
                console.error(ex);
            }
        }

        return { configData: { config: _config ? _config : [], slots: _slots ? _slots : [], pageName }, latePageN: _latePageN };

    }
    const setClipboard = (outText) => {
        navigator.clipboard.writeText(outText).then(function () {
            console.log("Copied to clipboard successfully!");
        }, function () {
            console.error("Unable to write to clipboard.");
        });
    }

    const generate = () => {
        const { configData, latePageN } = inputFromValue(inConfig, inSlots, inPageName);
        const outText = configGeneration(configData, inSsp, isRopChecked);
        setOutput(outText);
        if (isClipBChecked) {
            if (latePageN) {
                // after windows function prompt it takes a while for using the windows navigator!
                setTimeout(() => { setClipboard(outText); }, 180)
            } else {
                setClipboard(outText);
            }
        }
    }

    return (
        <SimpleFlexWrapper top="50px">
            <WrapAreas>
                <UnitName margin={false} onChange={(e) => setUnitName(e.target.value)} />
                <StandardInput placeholder="Initially expanded" />
                <StandardInput placeholder="PriorityLoad" />
                <StandardInput placeholder="Collapse empty div" />
                <StandardInput placeholder="Header bidding" />
                <StandardInput placeholder="Lazy load" />
                <StandardInput placeholder="Ignore" />
                <ConfigWrapper>

                    <ConfigSetting>
                        <Slider name={"Use data for config generation"} changeDesign={true} checked={isRopChecked} onChange={() => setIsRopChecked(!isRopChecked)} />
                        <Slider name={"Save to clipboard"} checked={isClipBChecked} onChange={() => setIsClipBChecked(!isClipBChecked)} />
                    </ConfigSetting>
                </ConfigWrapper>
                <InputField placeholder="Size" height="15%" onChange={(e) => setInConfig(e.target.value)} />
                < DistanceInput />
                <InputField placeholder="Size Mapping" onChange={(e) => setInSlots(e.target.value)} />
                <InputField placeholder="Targeting" onChange={(e) => setInSlots(e.target.value)} />
                <OutputField style={{ color: (output === "Output") ? " grey" : null }} dangerouslySetInnerHTML={{ __html: output }}></OutputField>
                <Button name={"Generate"} onClick={generate} top="14px" justify="flex-end" />
            </WrapAreas>
        </SimpleFlexWrapper>
    );
}

export { SlotsGeneration };
