import React, { useState } from 'react';
import styled from 'styled-components'

const SliderWrapper = styled.div`
    display: inline-block;
    position: relative;
    width: 60px;
    height: 24px;
`;



// ${CheckBox}:focus & {
//     box-shadow: 0 0 1px #2196F3;



// } 
// ${CheckBox}:checked & {
//     -webkit-transform: translateX(26px);
//     -ms-transform: translateX(26px);
//     transform: translateX(26px);
// }




const Slider = () => {

    const [isChecked, setIsChecked] = useState(true);

    const CheckBox = styled.input`
                opacity: 0;
                width: 0;
                height: 0;
            `;

    const SliderRound = styled.span`
            position: absolute;
            cursor: pointer;
            top: 0;
            left: 0;
            right: 0;
            bottom: 0;
            background-color: #ccc;
            -webkit-transition: .4s;
            transition: .4s;
            border-radius: 24px;

            &:before {

        border-radius: 50%;
        position: absolute;
        content: "";
        height: 30px;
        width: 30px;
        left: 1px;
        bottom: -3px;
        background-color: white;
        -webkit-transition: .4s;
        transition: .4s;
    }

    ${({ isChecked }) => isChecked && `
        background-color: #1a73e8;
    `};
`;

    return (
        <div>
            <SliderWrapper>
                <CheckBox type="checkbox" onChange={() => console.log('ooooo', isChecked)} />
                {/* <CheckBox type="checkbox" onChange={() => {console.log('ooooo', isChecked );setIsChecked(!isChecked)}} /> */}
                <SliderRound/>
            </SliderWrapper>

            <input type="checkbox" onChange={() => console.log('ooooo', isChecked)} />
        </div>




    );

}




export { Slider };
