import React, { useState } from 'react';
import styled from 'styled-components'
import { Label } from '../Label';


const ComponentWrapper = styled.div`
    margin-bottom: 10px;
`;

const SelectWrapper = styled.label`
    display: inline-block;
    position: relative;
    width: ${props => (props.width ? props.width : '120px')};
    height: 26px;
    margin: 0 0 -8px ${props => (props.distance ? props.distance : '10px')};
    border-bottom: 1px solid  ;
    padding 0px 0 2px 0;

`;


const Select = styled.select`
    font-size: 20px;
    font-weight: 500
    letter-spacing: .25px;
    width: ${props => (props.width ? props.width : '120px')};
    height: 24px;
    border: none;
    outline: none;
    background-color: transparent;

    // border-bottom: 1px solid ;
    // border-radius: none;
    // border-radius: 0px;



    // opacity: 0;
    position: absolute;
    top: 0;
    left: 0;
    right: 0;
    bottom: 0;

`;

const DropdownDesign = styled.span`
    position: absolute;
    cursor: pointer;
    top: 0;
    left: 0;
    right: 0;
    bottom: 0;

    // font-size: 20px;
    // font-weight: 500
    // letter-spacing: .25px;
    // border-radius: 24px;
    padding 0 0 0 -2px:
    z-index: 2;
    // border-bottom: 1px solid  ;
    border-bottom: 1px solid  ;
    padding 0px 0 9px 0;
    background-color: green;


`;


const Option = styled.option`
    // padding: 0 0 -60px; 
    // border-radius: 0px;
`;

const Dropdown = (props) => (
    <ComponentWrapper>
        <Label>
            {props.name}
        </Label>
        <SelectWrapper>
            {/* <DropdownDesign /> */}
            <Select>
                {props.sspNames.map((x, y) => <Option key={y + x}>{x}</Option>)};
            </Select>
        </SelectWrapper>
    </ComponentWrapper >
);

export { Dropdown };
