import React, { useState } from 'react';
import styled, { css } from 'styled-components'
import { Label } from '../Label';


const ComponentWrapper = styled.div`
    margin-bottom: 10px;
`;


const SliderWrapper = styled.label`
    display: inline-block;
    position: relative;
    width: 60px;
    height: 24px;
    margin: 0 0 -5.5px ${props => (props.distance ? props.distance : '10px')};
    @media(max-width: 1400px) {
        margin-left ${props => (props.changeDesign ? '126px' : null)}
    }
`;

const Checkbox = styled.input`
    opacity: 0;
    position: absolute;
    top: 0;
    left: 0;
    right: 0;
    bottom: 0;
`;


const SliderRound = styled.span`
    position: absolute;
    cursor: pointer;
    top: 0;
    left: 0;
    right: 0;
    bottom: 0;
    -webkit-transition: .6s;
    transition: .6s;
    border-radius: 24px;
    background-color: ${props => props.bColor};
    transition: 500ms;
    transform: translateX(${props => (props.checked ? "0" : "100px")});
    &:before {
        border-radius: 50%;
        position: absolute;
        content: "";
        height: 30px;
        width: 30px;
        left: 1px;
        bottom: -3px;
        background-color: F8F8FF;
        background-color: ${props => props.fColor};
        -webkit-transition: .6s;
        -ms-transition: .6s;
        transition:  .6s;
            ${props => props.checked && ({
        '-webkit-transform': 'translateX(27px)',
        '-ms-transform': 'translateX(27px)',
        transform: 'translateX(27px)',
    })}

`;

const Red = styled.div`
  width: 100%;
  height: 12px;
  background-color: red;
  flex: 0 0 100%;
  transition: 500ms;
  transform: translateX(${props => (props.onStart ? "0" : "100px")});
`;

const Slider = (props) => (
    <ComponentWrapper>
        <Label>
            {props.name}
        </Label>
        <Red checked={props.checked}/>
        <SliderWrapper distance={props.distance} changeDesign={props.changeDesign}>
            <SliderRound bColor={props.checked ? '#6da4ed' : '#ccc'} fColor={props.checked ? '#1a73e8' : '#F8F8FF'} checked={props.checked} />
            <Checkbox type="checkbox" {...props} />
        </SliderWrapper>
    </ComponentWrapper >
);

export { Slider };
